mapper.select:
SELECT COUNT(*) FROM CAMPING
WHERE 1=1

   AND STATUS = #{status}


   AND REGID = #{regid}


   AND ADDRESS1 = #{address1}


   AND ADDRESS2 = #{address2}


   AND REGEXP_LIKE(CAMP_TYPE, #{camptype})


   AND REGEXP_LIKE(THEMECODE, #{themecode})


   AND REG_KEYWORD LIKE '%'|| #{regkeyword} || '%'


   AND NAME LIKE '%' || #{searchName} || '%'

mapper.update:
UPDATE CAMPING
NAME = #{name}, CAMP_TYPE = #{camptype}, THEMECODE = #{themecode}, REG_KEYWORD = #{regkeyword}, SUMMARY = #{summary}, IMGURL = #{imgurl}, OPTIONS = #{options}, CONTENTS = #{contents}, POSTCODE = #{postcode}, ADDRESS1 = #{address1}, ADDRESS2 = #{address2}, ADDRESS3 = #{address3}, ADDRESS4 = #{address4}, LATITUDE = #{latitude}, LONGITUDE = #{longitude}, CONTACT = #{contact}, MODDATE = SYSDATE, MODID = #{modid}
WHERE NUM = #{num}

UPDATE CAMPING

	READCOUNT = READCOUNT+1

WHERE NUM = #{num}
