mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY NUM DESC, REGDATE DESC) AS ROW_NUM, NUM, REVIEW_NUM, (CASE REVIEW_TYPE WHEN '01' THEN '캠핑/글램핑 후기' WHEN '02' THEN '관광지 후기' WHEN '03' THEN '커뮤니티 후기' END) AS REVIEW_TYPE, IMGURL, RESERVATION_NUM, TITLE, SUMMARY, READCOUNT, RECOMMEND, STEAMED, STAR_RANKING, REG_KEYWORD, (CASE STATUS WHEN '01' THEN '노출' WHEN '02' THEN '비노출' END) AS STATUS, REGID, REGDATE FROM COMMUNITY WHERE 1=1
AND STATUS = #{status}
AND REGID = #{regid}
AND REVIEW_NUM = #{reviewnum}
AND REVIEW_TYPE = #{reviewtype}
AND REVIEW_TYPE NOT IN (#{reviewtypeNotIn})
AND TITLE LIKE '%'|| #{keyword} || '%'
AND SUMMARY LIKE '%'|| #{keyword} || '%'
ORDER BY NUM DESC

		SELECT COUNT(*) AS CNT FROM COMMUNITY
		WHERE 1=1

			AND STATUS = #{status}


			AND REGID = #{regid}


			AND REVIEW_NUM = #{reviewnum}


			AND REVIEW_TYPE = #{reviewtype}


			AND REVIEW_TYPE NOT IN (#{reviewtypeNotIn})


 			AND TITLE LIKE '%'|| #{keyword} || '%'


 			AND SUMMARY LIKE '%'|| #{keyword} || '%'

mapper.update:
UPDATE COMMUNITY
READCOUNT = READCOUNT+1 RECOMMEND = RECOMMEND+1 STEAMED = STEAMED+1
WHERE NUM = #{num}

mapper.select:
SELECT 	ROUND(AVG(STAR_RANKING)) SVGSTAR
FROM 	COMMUNITY
WHERE 	REVIEW_NUM = #{reviewnum}
AND 	REVIEW_TYPE = #{reviewtype}
