mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY NUM DESC, REGDATE DESC) AS ROW_NUM, NUM, (CASE ACODE WHEN '1' THEN '캠핑/글램핑 ' WHEN '2' THEN '관광지' WHEN '3' THEN '커뮤니티' WHEN '4' THEN '문의' END) AS ACODE, TAG, REGDATE, REGID FROM KEYWORD WHERE 1=1
AND ACODE = #{acode}
AND TAG LIKE '%'|| #{keyword} || '%'

SELECT COUNT(*) AS CNT FROM KEYWORD WHERE 1=1
AND ACODE = #{acode}
AND TAG LIKE '%'|| #{keyword} || '%'

mapper.update:
UPDATE KEYWORD
TAG = #{tag}, MODID = #{modid}, MODDATE = SYSDATE
WHERE NUM = #{num}
