mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY T1.NUM DESC, T1.REGDATE DESC) AS ROW_NUM, T1.NUM, TO_CHAR(T1.CHECKINDATE,'YYYY-MM-DD') AS CHECKINDATE, TO_CHAR(T1.CHECKOUTDATE,'YYYY-MM-DD') AS CHECKOUTDATE, TO_CHAR(T1.REGDATE,'YYYY-MM-DD') AS REGDATE, T1.GUESTS, T1.ID, T1.NAME, T1.PHONE, T1.EMAIL, T1.REQUESTED, T1.TOTALPRICE, T1.STATUS, T2.NUM AS CAMPING_NUM, T2.NAME AS CAMPING_NAME, T3.NAME AS ROOM_NAME FROM RESERVATION T1, CAMPING T2, ROOM T3 WHERE 1=1 AND T1.ID= #{id} AND T1.STATUS= #{status}
AND T1.ID LIKE '%'|| #{keyword} || '%'
AND T1.CNUM = T2.NUM AND T1.RNUM = T3.NUM AND T2.NUM = T3.CNUM ORDER BY T1.NUM DESC

SELECT COUNT(*) COUNT FROM RESERVATION T1, CAMPING T2, ROOM T3 WHERE 1=1 AND T1.ID= #{id} AND T1.STATUS= #{status}
AND T1.ID LIKE '%'|| #{keyword} || '%'
AND T1.CNUM = T2.NUM AND T1.RNUM = T3.NUM AND T2.NUM = T3.CNUM
