mapper.select:
SELECT DISTINCT C.NAME
FROM CAMPING C, ROOM R
WHERE C.NUM = #{cnum}

mapper.update:
UPDATE ROOM
NAME = #{name}, ROOM_TYPE = #{roomtype}, IMGURL = #{imgurl}, SUMMARY = #{summary}, CONTENTS = #{contents}, PROPGUESTS = #{propguests}, MAXGUESTS = #{maxguests}, WEEKDAYPRICE = #{weekdayprice}, WEEKENDPRICE = #{weekendprice}, OPTIONS = #{options}, AMENITY = #{amenity}, MODDATE = SYSDATE, MODID = #{modid}
WHERE NUM = #{num}

mapper.select:
SELECT CNUM
FROM ROOM
WHERE NUM = #{num}
