mapper.select:
		SELECT COUNT(*) FROM TOURLIST
		WHERE 1=1

			AND STATUS = #{status}


         AND REGID = #{regid}


			AND ADDRESS1 = #{address1}


			AND ADDRESS2 = #{address2}


			AND REGID NOT IN (#{aroundAddrRegId})


			AND REGEXP_LIKE(THEMECODE, #{themecode})


			AND REG_KEYWORD LIKE '%'|| #{regkeyword} || '%'


			AND NAME LIKE #{searchName}

mapper.update:
UPDATE TOURLIST
NAME = #{name}, THEMECODE = #{themecode}, PRICE = #{price}, DISCOUNT = ${discount}, REG_KEYWORD = #{regkeyword}, SUMMARY = #{summary}, IMGURL = #{imgurl}, CONTENTS = #{contents}, POSTCODE = #{postcode}, LATITUDE = #{latitude}, LONGITUDE = #{longitude}, CONTACT = #{contact}, MODDATE = SYSDATE, MODID = #{modid}
WHERE NUM = #{num}

UPDATE TOURLIST

	READCOUNT = READCOUNT+1

WHERE NUM = #{num}

UPDATE TOURLIST

	STEAMED = STEAMED+1

WHERE NUM = #{num}
