mapper.update:
update users set
name = #{name}, email = #{email}, birth = to_date(#{birth}), postcode = #{postcode},
address1 = #{address1}, address2 =  #{address2}, address3 = #{address3}, address4 = #{address4},
contact = #{contact}, moddate = sysdate, modid = #{modid}
where id = #{id}

mapper.select:
select count(*) from users

	where not ucode like 'admin'


	where not ucode like 'admin' and num like #{keyword}


	where not ucode like 'admin' and ucode like #{keyword}


	where not ucode like 'admin' and id like #{keyword}


	where not ucode like 'admin' and name like #{keyword}


	where not ucode like 'admin' and gender like #{keyword}


	where not ucode like 'admin' and email like #{keyword}


	where birth like #{keyword} and ucode like 'customer'


	where birth like #{keyword} and ucode like 'business'


	where not ucode like 'admin' and postcode like #{keyword}


	where ucode like 'business' and (address1 like #{keyword} or address2 like #{keyword} or address3 like #{keyword} or address4 like #{keyword})


	where not ucode like 'admin' and contact like #{keyword}


	where not ucode like 'admin' and regdate like #{keyword}


	where not ucode like 'admin' and moddate like #{keyword}


	where not ucode like 'admin' and modid like #{keyword}


	where not ucode like 'admin' and status like #{keyword}

select * from users

	where not ucode like 'admin'


	where not ucode like 'admin' and num like #{keyword}


	where not ucode like 'admin' and ucode like #{keyword}


	where not ucode like 'admin' and id like #{keyword}


	where not ucode like 'admin' and name like #{keyword}


	where not ucode like 'admin' and gender like #{keyword}


	where not ucode like 'admin' and email like #{keyword}


	where birth like #{keyword} and ucode like 'customer'


	where birth like #{keyword} and ucode like 'business'


	where not ucode like 'admin' and postcode like #{keyword}


	where not ucode like 'admin' and (address1 like #{keyword} or address2 like #{keyword} or address3 like #{keyword} or address4 like #{keyword})


	where not ucode like 'admin' and contact like #{keyword}


	where not ucode like 'admin' and regdate like #{keyword}


	where not ucode like 'admin' and moddate like #{keyword}


	where not ucode like 'admin' and modid like #{keyword}


	where not ucode like 'admin' and status like #{keyword}

order by num
